# -*- coding: utf-8 -*# Author:  Andy Ngo# Course:  CSCE 101# Assignment:  4 – Travel Management System, Python Classes# Date:  12 March 2018import csvimport sysfrom travelClass import travelItemdef readItems(itemsFileName, itemRecords) :    # readItems reads items from itemsFileName into the itemRecords dictionary    # Open itemsFileName and create a CSV file reader    itemsFile = open(itemsFileName, 'r')    itemsReader = csv.reader(itemsFile)    # Process each row of the items file    for row in itemsReader :        # Get the values for the record        (iid, iname, icount) = row        iid = str(iid)        iname = str(iname)        icount = int(icount)        if icount < 0:            print("""Found a travel item with an available start value less than 0.                     It will be skipped, but the program will continue.""")        else:            # Check if this ID is not yet in itemRecords            if (not(iid in itemRecords)) :                # Create a travelItem object and add it to itemRecords                itemRecord = travelItem(iid, iname, icount)                itemRecords[iid] = itemRecorddef printItems(itemRecords) :    # printItems prints the list of items from the itemRecords dictionary    # Print the header    print("ID     NAME")    print("-----  ------------")    # For each item record in itemRecords    for rec in itemRecords.values() :        # Print the item ID and name        print("{0:5s}  {1:12s}".format(rec.getID(), rec.getName()))    # Print the footer    print("")def readTransactions(transactionsFileName, itemRecords) :    transactionsFile = open(transactionsFileName, 'r')    transactionsReader = csv.reader(transactionsFile)    for row in transactionsReader :        (iid, transaction) = row        iid = str(iid)        transaction = int(transaction)        if (not(iid in itemRecords)) :            print("""Found an unknown iid when reading transactions. It was skipped,                     but the program will continue.""")        else:            itemRecord = itemRecords[iid]            itemRecord.appendTransaction(transaction)def printSummary(itemRecords) :    print("Tours")    print("=====")    print("ID     NAME          Start   Resv.   Cancl.  End ")    print("-----  ------------  ------  ------  ------  ------")    for rec in itemRecords.values() :        reservations = sum(rec.getReservations())        if rec.getAvailableEnd() < 0:            print("Error: {} had an available end less than 0. Exiting now.".format(rec.getName()))            sys.exit()        if rec.getAvailableEnd() > rec.getAvailableStart():            print("Error: {} had an available end less than 0. Exiting now.".format(rec.getName()))            sys.exit()        cancellations = sum(rec.getCancellations())        formatted_str = "{0:5s}  {1:12s}   {2:5}   {3:5}   {4:5}   {5:5}"        print(formatted_str.format(rec.getID(), rec.getName(),              rec.getAvailableStart(), reservations, cancellations,              rec.getAvailableEnd()))    print("")